import React, { useEffect, useState } from "react";

function UserManagmentPage() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [newUser, setNewUser] = useState({ username: "", password: "", role: "User" });
  const [editStates, setEditStates] = useState({}); // Track which users are in edit mode

  const token = localStorage.getItem("token");

  const fetchUsers = async () => {
    try {
      const res = await fetch("http://localhost:5268/api/SeeAllUsers", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!res.ok) throw new Error("Failed to fetch users.");
      const data = await res.json();
      setUsers(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchUsers();
    // eslint-disable-next-line
  }, []);

  const handleDelete = async (username) => {
    try {
      const res = await fetch("http://localhost:5268/api/DeleteUser", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ username }),
      });
      if (!res.ok) throw new Error("Failed to delete user.");
      fetchUsers();
    } catch (err) {
      console.error(err);
      alert("Error deleting user.");
    }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5268/api/CreateNewUser", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newUser),
      });
      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.message || "Failed to create user.");
      }
      setNewUser({ username: "", password: "", role: "User" });
      fetchUsers();
    } catch (err) {
      alert(err.message);
    }
  };

  const handleEditToggle = (username) => {
    setEditStates((prev) => ({
      ...prev,
      [username]: !prev[username],
    }));
  };

  const handleUsernameChange = async (oldUsername, newUsername) => {
    if (!newUsername || newUsername.trim() === "" || oldUsername === newUsername) {
      alert("New username cannot be empty or same as old one.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5268/api/ChangeUsername", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          OldUsername: oldUsername,
          NewUsername: newUsername.trim(),
        }),
      });

      let data;
      try {
        data = await res.json();
      } catch (jsonErr) {
        data = {};
      }

      if (!res.ok) {
        // Try to show a user-friendly error
        if (data?.message && data.message.toLowerCase().includes("already taken")) {
          alert("This username is already in use. Please choose another one.");
        } else if (data?.message) {
          alert(data.message);
        } else {
          alert("Failed to update username. The username may already exist or does not match the required pattern.");
        }
        return;
      }

      setEditStates((prev) => ({ ...prev, [oldUsername]: false }));
      fetchUsers();
    } catch (err) {
      alert("An error occurred while updating the username.");
    }
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Arial" }}>
      <h2>User Management</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleCreate} style={{ marginBottom: "20px" }}>
        <h3>Create New User</h3>
        <input
          type="text"
          placeholder="Username"
          value={newUser.username}
          onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          required
        />
        <select
          value={newUser.role}
          onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
        >
          <option value="User">User</option>
          <option value="Admin">Admin</option>
        </select>
        <button type="submit">Create</button>
      </form>

      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th style={{ borderBottom: "1px solid #ccc", padding: "8px" }}>ID</th>
            <th style={{ borderBottom: "1px solid #ccc", padding: "8px" }}>Username</th>
            <th style={{ borderBottom: "1px solid #ccc", padding: "8px" }}>Role</th>
            <th style={{ borderBottom: "1px solid #ccc", padding: "8px" }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => {
            const isEditing = editStates[user.username];
            return (
              <tr key={user.id}>
                <td style={{ padding: "8px", borderBottom: "1px solid #eee" }}>{user.id}</td>
                <td style={{ padding: "8px", borderBottom: "1px solid #eee" }}>
                  {isEditing ? (
                    <form
                      onSubmit={e => {
                        e.preventDefault();
                        handleUsernameChange(user.username, e.target.elements[0].value);
                      }}
                      style={{ display: "flex", alignItems: "center" }}
                    >
                      <input
                        type="text"
                        defaultValue={user.username}
                        style={{
                          width: "180px",
                          fontSize: "16px",
                          padding: "8px 12px",
                          marginRight: "10px",
                          borderRadius: "4px",
                          border: "1px solid #bbb",
                        }}
                      />
                      <button type="submit" style={{ marginRight: "6px", padding: "8px 16px" }}>Save</button>
                      <button type="button" onClick={() => handleEditToggle(user.username)} style={{ padding: "8px 16px" }}>Cancel</button>
                    </form>
                  ) : (
                    <div style={{ display: "flex", alignItems: "center" }}>
                      <span style={{ fontSize: "16px", marginLeft: "8px" }}>{user.username}</span>
                      <button onClick={() => handleEditToggle(user.username)} style={{ marginLeft: "16px", padding: "6px 14px" }}>Edit</button>
                      <button onClick={() => handleDelete(user.username)} style={{ marginLeft: "8px", padding: "6px 14px" }}>Delete</button>
                    </div>
                  )}
                </td>
                <td style={{ padding: "8px", borderBottom: "1px solid #eee" }}>{user.role}</td>
                <td style={{ padding: "8px", borderBottom: "1px solid #eee" }}>
                  <button onClick={() => handleEditToggle(user.username)}>
                    {isEditing ? "Cancel" : "Edit"}
                  </button>
                  <button onClick={() => handleDelete(user.username)}>Delete</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default UserManagmentPage;
